
public class OperatorEx10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		대입 연산자
		
		int n = 0;
		
		n += 3;		//단축 연산자 (밑에 형식으로 적는 것 보다 출력이 빠르다, 하지만 가독성을 위해서 특별한 상황이 아닌 이상 밑에 형식으로 적는다.)
//		n = n + 3;
		System.out.println(n);
		n *= 3;
//		n = n * 3;
		System.out.println(n);
		n = 10;
		System.out.println(n);
		n %= 3;
//		n = n % 3;
		System.out.println(n);
		
	}

}