
public class OperatorEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		논리부정 연산자 - ! (only for boolean. opposite of...)
		boolean flag = false;
//		가독성
//		클린 코드
		System.out.println(flag);
		flag = !flag;
		
		System.out.println(flag);
		
		System.out.println(!flag);
		
		System.out.println(flag);

	}

}
