
public class OperatorEx8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		단항 연산자 ++,,-- (변수에만 쓸 수 있음)
//		증감 연산자
		int n = 5;
		n++;	// n = n + 1 과 같은 것이다.
		
		System.out.println(n);
		
		n = 5;
		++n;	// n = n + 1 과 같은 것이다.
		System.out.println(n);
		
		n--;	// n = n - 1  과 같은 것
		--n;
		System.out.println(n);
		
	}

}