
/*
 * 무조건 while문으로
 * ======================
 * 4 - I can do it.
 * 3 - I can do it.
 * 2 - I can do it.
 * 1 - I can do it.
 * 0 - I can do it.
 * 
*/
public class ForTest10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 4;
		
		while(num >= 0) {
			System.out.println(num + " - I can do it.");
			num--;
		}

	}

}
